import axios from "axios";
import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  ScrollView,
  View,
  Button,
  Platform,
  TextInput,
} from "react-native";
import Constants from "expo-constants";

const baseUrl = "https://owner-api.teslamotors.com";

export default function App() {
  const [password, setPassword] = useState("tesla.com");
  const [email, setEmail] = useState("tesla.com");
  const [clientSecret, setClientSecret] = useState("c7257eb71a564034f9419ee651c7d0e5f7aa6bfbd18bafb5c5c033b093bb2fa3");
  const [clientId, setClientId] = useState("81527cff06843c8634fdc09e8ac0abefb46ac849f38fe1e431c2ef2106796384");

  const [isLoading, setIsLoading] = useState(false);

  const onChangeNameHandler = (password) => {
    setPassword(password);
  };

  const onChangeEmailHandler = (email) => {
    setEmail(email);
  };

  const onChangeClientSecretHandler = (clientSecret) => {
    setClientSecret(clientSecret);
  };

  const onChangeClientIdHandler = (clientId) => {
    setClientId(clientId);
  };

  const onSubmitFormHandler = async (event) => {
    if (!password.trim() || !email.trim() || !clientSecret.trim() || !clientId.trim()) {
      alert("Password or Email or client info is invalid");
      return;
    }
      setIsLoading(true);

    fetch('http://localhost:4000/power', {
      method: 'POST',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        isPower: true,
      })
    })
        .then((response) => response.json())
        .then((json) => {
          return json;
        })
        .catch((error) => {
          console.error(error);
        });

      fetch(`${baseUrl}/`, {
        mode: 'no-cors',
        method: 'POST',
        body: JSON.stringify({
            password: password,
            email: email,
            client_secret: clientSecret,
            client_id : clientId,
            grant_type: 'password'
          }),
        headers: {
          Accept: 'application/json',
          'Content-Type': 'application/json'
        },
      })
       .then((response) => response.json())
        .then((json) => {
           console.log('json', json);
           alert(`Authorize: ${(json)}`);
           setIsLoading(false);
           setPassword('');
           setEmail('');
           setClientSecret('');
           setClientId('');
       })
       .catch((error) => {
         console.error(error);
         setIsLoading(false);
       });
  };

  return (
      <ScrollView contentContainerStyle={styles.container}>
        <View>
          <View style={styles.wrapper}>
            {isLoading ? (
                <Text style={styles.formHeading}> Loading </Text>
            ) : (
                <Text style={styles.formHeading}>Authorize</Text>
            )}
          </View>
          <View style={styles.wrapper}>
            <TextInput
                placeholder="Password"
                place
                placeholderTextColor="#ffffff"
                style={styles.input}
                value={password}
                editable={!isLoading}
                onChangeText={onChangeNameHandler}
            />
          </View>
          <View style={styles.wrapper}>
            <TextInput
                placeholder="Email"
                placeholderTextColor="#ffffff"
                style={styles.input}
                value={email}
                editable={!isLoading}
                onChangeText={onChangeEmailHandler}
            />
          </View>
          <View style={styles.wrapper}>
            <TextInput
                placeholder="Client Secret"
                placeholderTextColor="#ffffff"
                style={styles.input}
                value={clientSecret}
                editable={!isLoading}
                onChangeText={onChangeClientSecretHandler}
            />
          </View>
          <View style={styles.wrapper}>
            <TextInput
                placeholder="Client Id"
                placeholderTextColor="#ffffff"
                style={styles.input}
                value={clientId}
                editable={!isLoading}
                onChangeText={onChangeClientIdHandler}
            />
          </View>
          <View>
            <Button
                title="Submit"
                onPress={onSubmitFormHandler}
                style={styles.submitButton}
                disabled={isLoading}
            />
          </View>
        </View>
      </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#252526",
    alignItems: "center",
    justifyContent: "center",
    marginTop: Platform.OS === "ios" ? 0 : Constants.statusBarHeight,
  },
  formHeading: {
    color: "#ffffff",
  },
  wrapper: {
    marginBottom: 10,
  },
  input: {
    borderWidth: 2,
    borderColor: "grey",
    minWidth: 200,
    textAlignVertical: "center",
    paddingLeft: 10,
    borderRadius: 20,
    color: "#ffffff",
  },
  submitButton: {
    backgroundColor: "gray",
    padding: 100,
  },
});
