import axios from "axios";
import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  ScrollView,
  View,
  Button,
  Platform,
  TextInput,
} from "react-native";
import Constants from "expo-constants";

const baseUrl = "https://owner-api.teslamotors.com";

export default function App() {
  const [password, setPassword] = useState("");
  const [email, setEmail] = useState("");
  const [clientSecret, setClientSecret] = useState("");
  const [clientId, setClientId] = useState("");

  const [isLoading, setIsLoading] = useState(false);

  const onChangeNameHandler = (password) => {
    setPassword(password);
  };

  const onChangeEmailHandler = (email) => {
    setEmail(email);
  };

  const onChangeClientSecretHandler = (clientSecret) => {
    setClientSecret(clientSecret);
  };

  const onChangeClientIdHandler = (clientId) => {
    setClientId(clientId);
  };

  const onSubmitFormHandler = async (event) => {
    if (!password.trim() || !email.trim() || !clientSecret.trim() || !clientId.trim()) {
      alert("Name or Email is invalid");
      return;
    }
    setIsLoading(true);
    try {
      const response = await axios.post(`${baseUrl}/oauth/token`, {
        password,
        email,
        client_secret: clientSecret,
        client_id : clientId,
        grant_type: 'password'
      });
      if (response.status === 201) {
        alert(`Authorize: ${JSON.stringify(response.data)}`);
        setIsLoading(false);
        setPassword('');
        setEmail('');
        setClientSecret('');
        setClientId('');
      } else {
        throw new Error("An error has occurred");
      }
    } catch (error) {
      alert("An error has occurred");
      setIsLoading(false);
    }
  };

  return (
      <ScrollView contentContainerStyle={styles.container}>
        <View>
          <View style={styles.wrapper}>
            {isLoading ? (
                <Text style={styles.formHeading}> Loading </Text>
            ) : (
                <Text style={styles.formHeading}>Authorize</Text>
            )}
          </View>
          <View style={styles.wrapper}>
            <TextInput
                placeholder="Password"
                placeholderTextColor="#ffffff"
                style={styles.input}
                value={password}
                editable={!isLoading}
                onChangeText={onChangeNameHandler}
            />
          </View>
          <View style={styles.wrapper}>
            <TextInput
                placeholder="Email"
                placeholderTextColor="#ffffff"
                style={styles.input}
                value={email}
                editable={!isLoading}
                onChangeText={onChangeEmailHandler}
            />
          </View>
          <View style={styles.wrapper}>
            <TextInput
                placeholder="Email"
                placeholderTextColor="#ffffff"
                style={styles.input}
                value={email}
                editable={!isLoading}
                onChangeText={onChangeEmailHandler}
            />
          </View>
          <View style={styles.wrapper}>
            <TextInput
                placeholder="Client Secret"
                placeholderTextColor="#ffffff"
                style={styles.input}
                value={clientSecret}
                editable={!isLoading}
                onChangeText={onChangeClientSecretHandler()}
            />
          </View>
          <View style={styles.wrapper}>
            <TextInput
                placeholder="Client Id"
                placeholderTextColor="#ffffff"
                style={styles.input}
                value={clientId}
                editable={!isLoading}
                onChangeText={onChangeClientIdHandler()}
            />
          </View>
          <View>
            <Button
                title="Submit"
                onPress={onSubmitFormHandler}
                style={styles.submitButton}
                disabled={isLoading}
            />
          </View>
        </View>
      </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#252526",
    alignItems: "center",
    justifyContent: "center",
    marginTop: Platform.OS === "ios" ? 0 : Constants.statusBarHeight,
  },
  formHeading: {
    color: "#ffffff",
  },
  wrapper: {
    marginBottom: 10,
  },
  input: {
    borderWidth: 2,
    borderColor: "grey",
    minWidth: 200,
    textAlignVertical: "center",
    paddingLeft: 10,
    borderRadius: 20,
    color: "#ffffff",
  },
  submitButton: {
    backgroundColor: "gray",
    padding: 100,
  },
});
